package corejava;

public class SingleDimensionalString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
	String a[] = new String[10];
		a[0]="wet"; //initialization
		a[1]="vfr";
		a[2]="gter";
		a[3]="bol";
		a[4]="vfr";
		a[5]="gtedr";
		a[6]="sf";
		a[7]="red";
		a[8]="ver";
		a[9]="fg";
		
		//traversing array
		for(int i=0;i<a.length;i++) {
			System.out.println(a[i]);
	}

}
}