package corejava;

public class implicitexplicit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//implicit coversion
		int a = 44;
		
		double d= a;
	 System.out.println("double value:"+d);
	   
	 
	 
	 //explicit coversion
	 double e =21.03;
	
	 int i = (int)e;
	 System.out.println("int value:"+i);
	 
		
		
		
	}

}
